<template>
  <div>
  <div v-for="elem in stack" v-bind:style="{ backgroundColor: getColorItem(elem) }" class="item">
    <img v-if="getSign(elem)" :src="getSign(elem)">
    <h2 v-else> {{ elem.sign }} </h2>
  </div>
  </div>
</template>

<script>
  export default {
    props: ['stack'],
    data () {
      return {
        signSources: {
          turnLeft: 'data:image/svg+xml;utf8;base64,',
          straight: 'data:image/svg+xml;utf8;base64,',
          turnRight: 'data:image/svg+xml;utf8;base64,'
        }
      }
    },
    methods: {
      getColorItem (item) {
        return item.color !== null ? item.color : 'grey'
      },
      getSign (item) {
        return this.signSources[item.sign]
      }
    }
  }
</script>

<style>
  .item {
    width: 30px;
    height: 30px;
    float: left;
    background-color: grey;
    border: 5px solid white;
  }
</style>
